import React from "react";
import { useDispatch, useSelector } from "react-redux";

import createStyles from "@guestyci/foundation/createStyles";

import Section from "@guestyci/foundation/Layout/Section";

import { Tabs, Tab } from "@guestyci/history/NavTabs";
import { NavLink } from "react-router-dom";
import { signout } from "../actions/accountActions";
import { signoutUser } from "../actions/userActions";

const useStyles = createStyles(
  (theme) => ({
    root: {
      backgroundColor: theme.palette.background.white,
      padding: `0 ${theme.spacer(7)}px`,
      height: 30,
      minHeight: 30,
      boxShadow: "0 0 10px 0 rgba(0, 0, 0, 0.2)",
      zIndex: 1,
    },
    fade: {
      position: "relative",
      top: -2,
    },
  }),
  { name: "NavBar" }
);

const NavBar = () => {
  // const accountSignin = useSelector((state) => state.accountSignin);
  // const {
  //   accountInfo: accountInfoSignIn,
  //   loading: loadingSignIn,
  //   error: errorSignIn,
  // } = accountSignin;

  const accountSignin = useSelector((state) => state.accountSignin);
  const { accountInfo } = accountSignin;
  // console.log('accountInfo:', accountInfo)

  const userSignin = useSelector((state) => state.userSignin);
  const { userInfo } = userSignin;

  const dispatch = useDispatch();

  const classes = useStyles();

  const activeStyle = {
    fontWeight: "bold",
    color: "red",
    textDecoration: "none",
  };
  return (
    <Section className={classes.root}>
      <NavLink to="/" activeStyle={activeStyle} exact>
        Home
      </NavLink>

      {accountInfo && (
        <p style={{ marginLeft: "20px" }}>account: {accountInfo.name}</p>
      )}
      {accountInfo ? (
        <>
          <div
            onClick={() => dispatch(signout())}
            activeStyle={activeStyle}
            style={{ marginLeft: "20px" }}
            exact
          >
            sign out account
          </div>
        </>
      ) : (
        <>
          {" "}
          <NavLink
            to="/account/signin"
            activeStyle={activeStyle}
            style={{ marginLeft: "20px" }}
            exact
          >
            sign in account
          </NavLink>
        </>
      )}

      {userInfo && (
        <p style={{ marginLeft: "20px" }}>user:{userInfo.firstName}</p>
      )}

      <NavLink
        to="/user/list"
        activeStyle={activeStyle}
        style={{ marginLeft: "20px" }}
        exact
      >
        User List
      </NavLink>
      { accountInfo && userInfo ? (
        <div
          onClick={() => dispatch(signoutUser())}
          activeStyle={activeStyle}
          style={{ marginLeft: "20px" }}
          exact
        >
          sign out user
        </div>
      ) : accountInfo && (
        <NavLink
          to="/user/signin"
          activeStyle={activeStyle}
          style={{ marginLeft: "20px" }}
          exact
        >
          signin user
        </NavLink>
      )}

      <NavLink
        to="/user/register"
        activeStyle={activeStyle}
        style={{ marginLeft: "20px" }}
        exact
      >
        add new user
      </NavLink>

      {/* <Tabs
          noAnimate
          noBorderBottom
          exact={false}
        >
            <Tab
              to="/"
              value={{path: '/', label: 'usersTable', src: '/'}}
              key={`/`}
            >


        users Table
            </Tab>
            <Tab
              to={"/user/add"}
              value={{path: '/user/add', label: 'userAdd', src: '/user/add'}}
              key={`/user/add`}
            >
        Add New User
            </Tab>     

        </Tabs>
    */}
    </Section>
  );
};

export default NavBar;
