import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";

import createStyles from "@guestyci/foundation/createStyles";

import Section from "@guestyci/foundation/Layout/Section";

import { Tabs, Tab } from "@guestyci/history/NavTabs";
import { NavLink } from "react-router-dom";
import { signout } from "../actions/accountActions";
import {
  detailsUser,
  signoutUser,
  signoutUserPreprod,
} from "../actions/userActions";
import { USER_DETAILS_RESET } from "../constants/userConstants";

const useStyles = createStyles(
  (theme) => ({
    root: {
      backgroundColor: theme.palette.background.white,
      padding: `0 ${theme.spacer(7)}px`,
      height: 30,
      minHeight: 30,
      boxShadow: "0 0 10px 0 rgba(0, 0, 0, 0.2)",
      zIndex: 1,
    },
    fade: {
      position: "relative",
      top: -2,
    },
  }),
  { name: "NavBar" }
);

const NavBar = () => {
  // const accountSignin = useSelector((state) => state.accountSignin);
  // const {
  //   accountInfo: accountInfoSignIn,
  //   loading: loadingSignIn,
  //   error: errorSignIn,
  // } = accountSignin;

  const accountSignin = useSelector((state) => state.accountSignin);
  const { accountInfo } = accountSignin;
  // console.log('accountInfo:', accountInfo)

  const userSignin = useSelector((state) => state.userSignin);
  const { userInfo } = userSignin;

  const userData = useSelector((state) => state.userData);
  const {
    userInfoData,
    accountData,
    loading: loadingUserData,
    error: errorUserData,
  } = userData;
  console.log("userData:", userData);

  const userUpdate = useSelector((state) => state.userUpdate);
  const {
    loading: loadingUpdate,
    error: errorUpdate,
    success: successUpdate,
  } = userUpdate;
  const userDetails = useSelector((state) => state.userDetails);
  const { loading, error: errorUserDetails, user } = userDetails;

  const dispatch = useDispatch();

  const classes = useStyles();

  const activeStyle = {
    fontWeight: "bold",
    color: "red",
    textDecoration: "none",
  };

  const [firstName, setFirstName] = useState();

  useEffect(() => {
    if (!user) {
      console.log("detailsUser");
      dispatch(detailsUser(userInfo._id));
    } else {
      setFirstName(user.firstName);
    }
  }, [user, successUpdate]);


  function handleSignOut() {
  dispatch(signoutUserPreprod())
  dispatch({ type: USER_DETAILS_RESET});
  }
  return (
    <>
      {accountData && userInfoData && (
        <Section className={classes.root}>
          <NavLink to="/" activeStyle={activeStyle} exact>
            Home
          </NavLink>

          {/* {accountData && (
      console.log("ðŸš€ ~ file: NavBar.js ~ line 95 ~ useEffect ~ detailsUser", detailsUser)
      console.log("ðŸš€ ~ file: NavBar.js ~ line 95 ~ useEffect ~ detailsUser", detailsUser)
      console.log("ðŸš€ ~ file: NavBar.js ~ line 95 ~ useEffect ~ detailsUser", detailsUser)
      console.log("ðŸš€ ~ file: NavBar.js ~ line 95 ~ useEffect ~ detailsUser", detailsUser)
            <NavLink
              style={{ marginLeft: "20px" }}
              to={`/account/info/${accountData._id}`}
              activeStyle={activeStyle}
              exact
            >
              account: {accountData.name}
            </NavLink>
          )} */}

          {accountData && (
            <div style={{ marginLeft: "20px" }} activeStyle={activeStyle}>
              account: {accountData.name}
            </div>
          )}

          {userInfoData && (
            <NavLink
              style={{ marginLeft: "20px" }}
              to={`/user/edit/${userInfoData.userId}/preprod`}
              activeStyle={activeStyle}
              exact
            >
              user:{userInfoData.fullName}
            </NavLink>
          )}
          <div
            onClick={handleSignOut}
            activeStyle={activeStyle}
            style={{ marginLeft: "20px" }}
            exact
          >
            sign out user
          </div>

          {userInfoData && (
            <>
              {" "}
              <NavLink
                to="/preprod/table"
                activeStyle={activeStyle}
                style={{ marginLeft: "20px" }}
                exact
              >
                User List
              </NavLink>
              <NavLink
                to="/blog/preprod/list"
                activeStyle={activeStyle}
                style={{ marginLeft: "20px" }}
                exact
              >
                Blog List
              </NavLink>
            </>
          )}
          {/* <NavLink
        to="/blog/list"
        activeStyle={activeStyle}
        style={{ marginLeft: "20px" }}
        exact
      >
        Account Blogs
      </NavLink> */}

          {/* <Tabs
          noAnimate
          noBorderBottom
          exact={false}
        >
            <Tab
              to="/"
