              key={`/`}
            >

        users Table
            </Tab>
            <Tab
              to={"/user/add"}
              value={{path: '/user/add', label: 'userAdd', src: '/user/add'}}
              key={`/user/add`}
            >
        Add New User
            </Tab>

        </Tabs>
    */}
        </Section>
      )}
      {/* ///////////////////////////////////////////////////////// */}
      {!accountData && !userInfoData && (
        <Section className={classes.root}>
          <NavLink to="/" activeStyle={activeStyle} exact>
            Home
          </NavLink>

          {accountInfo && (
            <NavLink
              style={{ marginLeft: "20px" }}
              to={`/account/info/${accountInfo._id}`}
              activeStyle={activeStyle}
              exact
            >
              account: {accountInfo.name}
            </NavLink>
          )}

          {accountInfo && (
            <>
              <div
                onClick={() => dispatch(signout())}
                activeStyle={activeStyle}
                style={{ marginLeft: "20px" }}
                exact
              >
                sign out account
              </div>
            </>
          )}

          {userInfo && !accountInfo && (
            <NavLink
              to="/account/signin"
              activeStyle={activeStyle}
              style={{ marginLeft: "20px" }}
              exact
            >
              sign in account
            </NavLink>
          )}

          {user ? (
            <>
              <NavLink
                style={{ marginLeft: "20px" }}
                to={`/user/info/${user._id}`}
                activeStyle={activeStyle}
                exact
              >
                user:{firstName}
              </NavLink>
              <div
                onClick={() => dispatch(signoutUser())}
                activeStyle={activeStyle}
                style={{ marginLeft: "20px" }}
                exact
              >
                sign out user
              </div>
            </>
          ) : (
            <NavLink
              to="/user/signin"
              activeStyle={activeStyle}
              style={{ marginLeft: "20px" }}
              exact
            >
              signin user
            </NavLink>
          )}

          {accountInfo && userInfo && (
            <>
              {" "}
              <NavLink
                to="/user/list"
                activeStyle={activeStyle}
                style={{ marginLeft: "20px" }}
                exact
              >
                User List
              </NavLink>
              <NavLink
                to="/blog/list"
                activeStyle={activeStyle}
                style={{ marginLeft: "20px" }}
                exact
              >
                Blog List
              </NavLink>
            </>
          )}
          {/* <NavLink
        to="/blog/list"
        activeStyle={activeStyle}
        style={{ marginLeft: "20px" }}
        exact
      >
        Account Blogs
      </NavLink> */}

          <NavLink
            to="/preprod/table"
            activeStyle={activeStyle}
            style={{ marginLeft: "20px" }}
            exact
          >
            Prepord Table
          </NavLink>

          {/* <Tabs
          noAnimate
          noBorderBottom
          exact={false}
        >
            <Tab
              to="/"
              value={{path: '/', label: 'usersTable', src: '/'}}
              key={`/`}
            >

        users Table
            </Tab>
            <Tab
              to={"/user/add"}
              value={{path: '/user/add', label: 'userAdd', src: '/user/add'}}
              key={`/user/add`}
            >
        Add New User
            </Tab>

        </Tabs>
    */}
        </Section>
      )}
    </>
  );
};

export default NavBar;
