    //     // }, 3000);
    //   }
    // }
    // createAccount();

    // props.history.push("/");
  };

  const closeError = () => {
    setError(false);
  };

  return (
    <Section className={classes.app} justify="center">
      <Col spacing={6} span={6}>
        <TextField variant="h2">
          Add Account:
          {name}
        </TextField>
        <FinalForm
          onSubmit={onSubmit}
          initialValues={{}}
          render={({ handleSubmit }) => (
            <Form
              title="Create Account"
              onSubmit={handleSubmit}
              provider={FormProvider.Final}
              fieldInstance={Field}
            >
              <FormGroup>
                <FormGroup>
                  <FormField
                    label="First Name"
                    name="name"
                    //   info="Drama, Action etc."
                  >
                    <Input
                      name="name"
                      value={name}
                      placeholder={name}
                      onChange={(e) => setName(e.target.value)}
                    />
                  </FormField>
                  <FormField label="Email" name="email">
                    <EmailInput
                      name="email"
                      value={email}
                      placeholder={email}
                      // validate={[email]}
                      onChange={(e) => setEmail(e.target.value)}
                    />
                  </FormField>

                  {/* 
                  <FormField label="Password" name="password">
                    <Input
                      name="password"
                      type={password}
                      value={password}
