export const prices = [
  {
    name: "Any",
    min: 0,
    max: 0,
  },
  {
    name: `$1 to $10`,
    min: 1,
    max: 10,
  },
  {
    name: `$10 to $100`,
    min: 10,
    max: 100,
  },
  {
    name: `$100 to $1000`,
    min: 100,
    max: 1000,
  },
];
export const ratings = [
  {
    name: "4stars & up",
    rating: 4,
  },

  {
    name: "3stars & up",
    rating: 3,
  },

  {
    name: "2stars & up",
    rating: 2,
  },

  {
    name: "1stars & up",
    rating: 1,
  },
];

export function makeDataForTable(data) {
  console.log('data:', data)
  const arr = [];

  for (let i = 0; i < data.length; i++) {
    // console.log("data[i]: ", data[i]);

    const arrPost = Object.entries(data[i]);
    console.log("arrPost:", arrPost);

    // 0: (2) ['_id', '6141c0d7874998b51b3febd5']
    // 1: (2) ['userId', '6141bd2a874998b51b3febd4']
    // 2: (2) ['title', 'cool post']
    // 3: (2) ['content', 'this is a very good post! ']
    // 4: (2) ['createdAt', '2021-09-15T09:45:59.885Z']
    // 5: (2) ['updatedAt', '2021-09-15T09:45:59.885Z']

    //  title: {
    //   children: 'John',
    // },

    const bigObj = {};

    for (let j = 0; j < arrPost.length; j++) {
      const obj = {};
      // console.log("arrPost[j]: ", arrPost[j]);
      // console.log("arrPost[0]: ",arrPost[j][0]);
      // console.log("arrPost[1]: ",arrPost[j][1]);

      // console.log("obj:", obj);

      if (arrPost[j][0] === "_id") {
        obj.children = arrPost[j][1];
        //         obj= {children: '613e616d7ab4e566768e79d5'}

        bigObj.id = obj;
        //    bigObj= {id: {children: '613e616d7ab4e566768e79d5'}}

        bigObj._id = arrPost[j][1];

        //   bigObj= {_id: '613e616d7ab4e566768e79d5'}
      } else if (
        arrPost[j][0] === "createdAt" ||
        arrPost[j][0] === "updatedAt"
      ) {
        // ['createdAt', '2021-09-13T08:46:49.849Z']

        obj.children = new Date(arrPost[j][1]).toLocaleString();
        // {children: '9/13/2021, 11:46:49 AM'}

        bigObj[arrPost[j][0]] = obj;

        // {createdAt: {children: '9/13/2021, 11:46:49 AM'}}
      } else if (arrPost[j][0] === "userId") {
        // console.log("arrPost:", arrPost);

        // ['userId',  {_id: '613e1486e5218626969838f0', name: 'Karamba'}]
        obj.children = `${arrPost[j][1].firstName} ${arrPost[j][1].lastName}`;

        // {children: 'Karamba'}

        let emailObj = {};

        emailObj.children = arrPost[j][1].email;

        bigObj["admin"] = obj;

        bigObj["email"] = emailObj;

        let accountObj = {};

        accountObj.children = arrPost[j][1].accountId.name;

        bigObj["account"] = accountObj;

        //{ accountId: {children: 'Karamba'}}
      } else if (arrPost[j][0] === "content") {
        // ['firstName', 'miki']

        obj.children = `${arrPost[j][1].slice(0, 50)}...`;

        // {children: 'miki'}

        bigObj[arrPost[j][0]] = obj;

        // firstName: {children: 'miki'}
      } else {
        // ['firstName', 'miki']

        obj.children = arrPost[j][1];

        // {children: 'miki'}

        bigObj[arrPost[j][0]] = obj;

        // firstName: {children: 'miki'}
      }

      // console.log("bigObj:", bigObj);

      // arr.push( `${arrPost[j][0]}: {children: '${arrPost[j][1]}',}`)
    }
    // arr.push({gilad:1,...data[i]})
    arr.push({ ...bigObj });
    // arr.push({ _id: `${counter}`, ...bigObj });

    // counter++;
  }

  console.log("arr: ", arr);

  return arr;
}
