import {
  BLOG_DELETE_FAIL,
  BLOG_DELETE_REQUEST,
  BLOG_DELETE_RESET,
  BLOG_DELETE_SUCCESS,
  BLOG_DETAILS_FAIL,
  BLOG_DETAILS_REQUEST,
  BLOG_DETAILS_RESET,
  BLOG_DETAILS_SUCCESS,
  BLOG_LIST_FAIL,
  BLOG_LIST_REQUEST,
  BLOG_LIST_SUCCESS,
  BLOG_REGISTER_FAIL,
  BLOG_REGISTER_REQUEST,
  BLOG_REGISTER_SUCCESS,
  BLOG_SIGNIN_FAIL,
  BLOG_SIGNIN_REQUEST,
  BLOG_SIGNIN_SUCCESS,
  BLOG_SIGNOUT,


  BLOG_UPDATE_FAIL,

  BLOG_UPDATE_REQUEST,
  BLOG_UPDATE_RESET,
  BLOG_UPDATE_SUCCESS,


} from "../constants/blogConstants";


export const blogRegisterReducer = (state = {}, action) => {
  switch (action.type) {
    case BLOG_REGISTER_REQUEST:
      return { loading: true };
    case BLOG_REGISTER_SUCCESS:
      return { loading: false, blogInfo: action.payload };
    case BLOG_REGISTER_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};

export const blogSigninReducer = (state = {}, action) => {
  switch (action.type) {
    case BLOG_SIGNIN_REQUEST:
      return { loading: true };
    case BLOG_SIGNIN_SUCCESS:
      return { loading: false, blogInfo: action.payload };
    case BLOG_SIGNIN_FAIL:
      return { loading: false, error: action.payload };
    case BLOG_SIGNOUT:
      return {};
    default:
      return state;
  }
};



export const blogDetailsReducer = (state = { loading: true }, action) => {
  switch (action.type) {
    case BLOG_DETAILS_REQUEST:
      return { loading: true };
    case BLOG_DETAILS_SUCCESS:
      return { loading: false, blog: action.payload };
    case BLOG_DETAILS_FAIL:
      return { loading: false, error: action.payload };
    case BLOG_DETAILS_RESET:
      return { loading: true };
    default:
      return state;
  }
};

export const blogUpdateReducer = (state = {}, action) => {
  switch (action.type) {
    case BLOG_UPDATE_REQUEST:
      return { loading: true };
    case BLOG_UPDATE_SUCCESS:
      return { loading: false, success: true };
    case BLOG_UPDATE_FAIL:
      return { loading: false, error: action.payload };
    case BLOG_UPDATE_RESET:
      return {};
    default:
      return state;
  }
};
export const blogListReducer = (state = { loading: true }, action) => {
  switch (action.type) {
    case BLOG_LIST_REQUEST:
      return { loading: true };
    case BLOG_LIST_SUCCESS:
      return { loading: false, blogs: action.payload };
    case BLOG_LIST_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};
export const blogDeleteReducer = (state = {}, action) => {
  switch (action.type) {
    case BLOG_DELETE_REQUEST:
      return { loading: true };
    case BLOG_DELETE_SUCCESS:
      return { loading: false, success: true };
    case BLOG_DELETE_FAIL:
      return { loading: false, error: action.payload };
    case BLOG_DELETE_RESET:
      return {};
    default:
      return state;
  }
};
