import { useState, useCallback } from 'react';

const useCollapse = (initialValue = true) => {
  const [isCollapsed, setCollapse] = useState(initialValue);
  const closeCollapse = useCallback(() => setCollapse(true));
  const openCollapse = useCallback(() => setCollapse(false));
  const toggleCollapse = useCallback(() => {
    setCollapse(!isCollapsed);
  });
  return [isCollapsed, toggleCollapse, openCollapse, closeCollapse];
};

export default useCollapse;