import PropTypes from 'prop-types';

const Column = () => {
  return null;
};

Column.defaultProps = {
  width: 200,
  fixed: undefined,
  align: 'left',
  dataKey: '',
  sortable: false,
  resizable: false
};
Column.propTypes = {
  /** Column width */
  width: PropTypes.number,

  /** Indicator whether or not column should be fixed (left | right)  */
  fixed: PropTypes.oneOf(['left', 'right']),

  /** Set content alignment  */
  align: PropTypes.oneOf(['left', 'center', 'right']),

  /** Data source to retrieve data from   */
  dataKey: PropTypes.string,

  /** Indicator whether or not column is sortable  */
  sortable: PropTypes.bool,

  /** Indicator whether or not column can be resized */
  resizable: PropTypes.bool
};
export default Column;