import isEmpty from 'lodash/isEmpty';

const getCellRenderer = ({
  isLoading,
  placeholder,
  cellRenderer
}) => data => {
  const {
    cellData,
    rowData
  } = data;

  if (isEmpty(rowData)) {
    return placeholder(isLoading);
  }

  if (cellRenderer) {
    return cellRenderer(data);
  }

  return cellData;
};

export default getCellRenderer;