const values = {
  xs: 0,
  sm: 576,
  md: 768,
  lg: 992,
  xl: 1200,
  xlg: 1900
};
export const keys = ['xs', 'sm', 'md', 'lg', 'xl', 'xlg'];
const units = 'px';

const create = key => {
  const value = typeof values[key] === 'number' ? values[key] : key;
  return `@media (min-width: ${value}${units})`;
};

export default {
  keys,
  values,
  create
};