import isFunction from 'lodash/isFunction';

function useComposeRefs(...refs) {
  return target => {
    refs.forEach(ref => {
      if (!ref) return;

      if (isFunction(ref)) {
        ref(target);
      } else {
        // eslint-disable-next-line no-param-reassign
        ref.current = target;
      }
    });
  };
}

export default useComposeRefs;