import _extends from "@babel/runtime/helpers/esm/extends";
import { useEffect, useRef } from 'react';
import { createUploadWidget } from "../utils/fileUploadUtility";
import { CLOUDINARY_URL } from "../constants/constants";
import useScript from "../useScript";

const useFileUpload = ({
  onSuccess,
  onFailure,
  rest
}) => {
  const cloudinaryWidget = useRef();
  const [loaded, error] = useScript(CLOUDINARY_URL);
  useEffect(() => {
    if (!cloudinaryWidget.current && loaded && !error && window.cloudinary) {
      cloudinaryWidget.current = createUploadWidget(_extends({
        onSuccess,
        onFailure
      }, rest));
    }

    if (error) {
      console.log('error', error);
    }
  }, [loaded, error, window.cloudinary]);

  const open = () => {
    if (loaded && !error) {
      cloudinaryWidget.current.open();
    }
  };

  return [open];
};

export default useFileUpload;