/**
 *  Get valid placeholder for input
 * @param placeholder {String}  Passed placeholder
 * @param required  {Boolean}  is the field required
 * @param disabled  {Boolean}  is the field disabled
 * @return {String} The final placeholder formatted
 */
export var getPlaceholder = function getPlaceholder(placeholder, required, disabled) {
  if (!required && !disabled) {
    return placeholder ? "".concat(placeholder, " (Optional)") : '(Optional)';
  }

  return placeholder;
};