import React from 'react';
import { mount, shallow } from 'enzyme';
/**
 * create the basic setup for components
 * @param comp  {function({currency?: *, value?: *}): *} The component to setup
 * @returns {function(*=, *): *}
 */

const setup = comp => (props, isMount, attachTo) => {
  const component = React.createElement(comp, props);
  return isMount ? mount(component, attachTo ? {
    attachTo
  } : undefined) : shallow(component);
};

export default setup;