import * as reactRedux from 'react-redux';
/**
 * Create  a stub for useSelector of react-redux
 */

export const useSelectorStub = () => {
  reactRedux.useSelector = jest.fn().mockImplementation(func => func());
};
/**
 * Create  a mock for useDispatch of react-redux
 * @returns {Function} mock for dispatch
 */

export const useDispatchMock = () => {
  const dispatch = jest.fn(action => action);
  reactRedux.useDispatch = jest.fn().mockImplementation(() => dispatch);
  return dispatch;
};