import { mount as enzymeMount, shallow as enzymeShallow } from 'enzyme';
/**
 * Create  a new container and append to document body
 * @param id  {String}  Container id
 * @return {HTMLElement}
 */

export const createContainer = id => {
  const container = document.createElement('div');
  container.setAttribute('id', id);
  document.body.appendChild(container);
  return container;
};
/**
 * Generate mock function for unit tests
 * @returns {*}
 */

export const mockFn = jest.fn;
/**
 * Clear all mocks
 * @returns {*}
 */

export const clearMocks = jest.clearAllMocks;
/**
 * Clear all mocks
 * @returns {*}
 */

export const {
  clearAllMocks
} = jest;
/**
 * Helper function to create context switch to await async operations
 * @returns {Promise<any>}
 */

export const asyncFlush = (count = 0) => new Promise(resolve => setTimeout(resolve, count));
export const mount = enzymeMount;
export const shallow = enzymeShallow;